<span class="line"><span style="color:#F07178;">        </span><span style="color:#A6ACCD;">brightness</span><span style="color:#F07178;"> </span><span style="color:#89DDFF;">=</span><span style="color:#F07178;"> </span><span style="color:#F78C6C;">0</span><span style="color:#89DDFF;">;</span></span>
